package Numberpattern;

public class p3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=5;
		int count=0;
		for(int i=1;i<5;i++)
		{
			for(int j=1;j<=i;j++)
			{
				count++;
				System.out.print(count);
				System.out.print(" ");
			}
			System.out.println(" ");
		}

	}

}
