package Switch;

public class p4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=50;
		switch (a)
		{
		case 10: System.out.println("Room no is 10");
		break;
		case 30: System.out.println("Room no is 30");
		break;
		case 20: System.out.println("Room no is 20");
		break;
	
		default: System.out.println(" I am not in this room");
		}

	}

}
