package charcterpattern;

public class p3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char count='a';
		for(char i='a';i<'e';i++)
		{
			for(int j='a';j<=i;j++)
			{
				System.out.print(count);
				count++;
				System.out.print(" ");
			}
			System.out.println(" ");
		}


	}

}
