package controlstatements;

public class swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		a=a+b;//30
		b=a-b;//30-20=10
		a=a-b;//30-10=20
		System.out.println(a);
		System.out.println(b);
		System.out.println("---------------");
		int c=10;
		int d=20;
		int temp;
		temp=c;//30
		c=d;//40
		d=temp;// 30
		System.out.println(c);
		System.out.println(d);

		
		
		

		
		

	}

}
